import { useContext, useEffect, useState } from 'react'
import { getBrands } from '../../../../../Api/brandsApi'
import WorkingSpaceContext from '../../../../../Context/WorkingSpaceContext'
import SelectBox from '../../../../globalComponents/SelectName/SelectBox'
import styles from '../ControlPanel.module.scss'

function CreateBrand() {
    return (
        <div>CreateBrand</div>
    )
}

function GetBrand() {

    const [current, setCurrent] = useState('')
    const [titleBrands, setTitleBrands] = useState([])
    const handlerClickItem = (newCurrent: string) => { setCurrent(newCurrent) }
    useEffect(() => {
        let data: string[] = []
        getBrands()
            .then((res) => {
                res.data.map((item) => {
                    data.push(item.title)
                }
                )
            })
        localStorage.setItem('titleBrands', JSON.stringify(data))
    }, [])
    return (

        <SelectBox
            current={current}
            handlerClick={handlerClickItem}
        ></SelectBox>
    )
}

function UpdateBrand() {
    return (
        <div>UpdateBrand</div>
    )
}
function DeleteBrand() {
    return (
        <div>DeleteBrand</div>
    )
}

function MethodBrands() {
    const value = useContext(WorkingSpaceContext)
    const state = value.controlPanelState
    if (state === 'Создать')
        return (<CreateBrand />)
    else if (state === 'Получить')
        return (<GetBrand />)
    else if (state === 'Обновить')
        return (<UpdateBrand />)
    else if (state === 'Удалить')
        return (<DeleteBrand />)
    else return (<div>Этот компонент пока не создан</div>)
}
export default function Brands() {
    return (
        <div className={styles['controlPanel-brand']}>
            <MethodBrands />
        </div>
    )
}
