import { useContext, useEffect, useState } from 'react'
import backend from '../../../../../Api/brandsApi'
import PanelContext from '../../../../../Context/PanelContext'
import WorkingSpaceContext from '../../../../../Context/WorkingSpaceContext'
import SelectBox from '../../../../globalComponents/SelectName/SelectBox'
import styles from '../ControlPanel.module.scss'

function CreateBrand() {
    return (
        <div>CreateBrand</div>
    )
}

function GetBrand() {
    const [brands, setBrands] = useState([''])
    const [brand, setBrand] = useState('')

    const value = useContext(PanelContext)

    const itemClickHandler = (newCurrent: string) => { setBrand(newCurrent) }

    async function fetchBrands() {
        const brands = await backend.getBrands()
        const titles: string[] = brands.map(brand => brand.title)
        titles.push('all')
        setBrands(titles)
    }

    async function fetchDataBrands() {
        const brands = await backend.getBrands()
        value.changeStore?.setBrands(JSON.stringify(brands))
    }

    useEffect(() => {
        fetchBrands()
    }, [])

    return (
        <>
            <SelectBox
                data={brands}
                current={brand}
                handlerClick={itemClickHandler}
            ></SelectBox>
            <button className={styles['controlPanel-submit']}>
                <div
                    onClick={fetchDataBrands}
                >Отправить запрос</div>
            </button>
        </>
    )
}

function UpdateBrand() {
    return (
        <div>UpdateBrand</div>
    )
}
function DeleteBrand() {
    return (
        <div>DeleteBrand</div>
    )
}

function MethodBrands() {
    const value = useContext(WorkingSpaceContext)
    const state = value.controlPanelState
    if (state === 'Создать')
        return (<CreateBrand />)
    else if (state === 'Получить')
        return (<GetBrand />)
    else if (state === 'Обновить')
        return (<UpdateBrand />)
    else if (state === 'Удалить')
        return (<DeleteBrand />)
    else return (<div>Этот компонент пока не создан</div>)
}
export default function Brands() {
    return (
        <div className={styles['controlPanel-brand']}>
            <MethodBrands />
        </div>
    )
}
