import styles from './header-slidingBox.module.scss'
import '../../../sours/icon/style.css'
import { useState } from 'react'

interface SlidingBoxProps {
    status?: 'default' | 'mobail',
    role?: string,
    info?: string | React.ReactNode
}

export default function SlidingBox({ status = 'default', role = 'unknown', info = 'fffff' }: SlidingBoxProps) {
    const [work, setWork] = useState(false)
    const [hoverd, setHoverd] = useState(false)
    const styleUserIcon = {
        border: '1px solid black',
        borderRadius: '50%'
    }

    const hendlerClick = (e: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
        setWork(!work)
    }
    const hendlerMouseOver = (e: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
        setHoverd(true)
    }
    const hendlerMouseOut = (e: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
        setHoverd(false)
    }

    let styleSlidingBox = {}
    if (hoverd) {
        styleSlidingBox = {
            background: 'rgb(200, 200, 200)'
        }
    }
    if (work) {
        styleSlidingBox = {
            background: 'rgb(190, 190, 190)',
            overflow: 'visible'
        }
    }

    return <div
        className={styles['slidingBox__' + status]}
        onMouseOver={hendlerMouseOver}
        onMouseOut={hendlerMouseOut}
        style={styleSlidingBox}
    >
        <div className={styles['slidingBox-authorization']}
            onClick={hendlerClick}
        >
            <div className={styles['slidingBox-icon_user']}>
                <i className='ic_user' style={styleUserIcon}></i>
            </div>
            <div className={styles['slidingBox-role']}>{role}</div>
            <div className={styles['slidingBox-icon_arrow_down']}>
                <i className={!work ? 'ic_arrow_down' : 'ic_arrow_up'}></i>
            </div>
        </div>
        <div className={styles['slidingBox-info']}>{info}</div>
    </div>

}
