import { useContext } from "react"
import backend from "../Api/brandsApi"
import { IBrand, IBrandDto } from "../Api/dto/IBrand"
import BrandsContext from "../Context/BrandsContext"

export default function useBrand() {

    const brandsContext = useContext(BrandsContext)

    const brand = (title: string) => {
        if (title === 'all')
            return brandsContext.brands?.all
        else
            return brandsContext.brands?.all.filter(brand => (brand.title === title) ? brand : '')
    }

    const createBrand = async (brand: IBrand) => {
        await backend.createBrand(brand)
            .then(brand => brandsContext.create ? brandsContext.create(brand) : console.log(brand))
    }

    const updateBrand = async (brand: IBrandDto) => {
        await backend.updateBrand(brand)
    }

    const deleteBrand = async (title: string) => {
        await backend.deleteBrand(title)
            .then(brand => brandsContext.delete ? brandsContext.delete(brand) : console.log(brand))
    }

    return {
        brands: brandsContext.brands,
        brand,
        create: createBrand,
        update: updateBrand,
        delete: deleteBrand
    }
}
