import { useContext, useState } from 'react'
import ControlPanelContext from '../../../../Context/ControlPanelContext'
import WorkingSpaceContext from '../../../../Context/WorkingSpaceContext'
import { RequestsParamsData } from '../../../../StaticData/RequestsParamsData'
import styles from './ControlPanel.module.scss'
import Item from './Item'


function CreateTabBlock() {
    const value = useContext(WorkingSpaceContext)
    const reqBlocks = RequestsParamsData[value.state].map((item, index) => {
        return (
            <Item
                key={index + item}
                callback={item}
            >{item}</Item>)
    })
    return (reqBlocks)
}

export default function ControlPanel() {
    const items = CreateTabBlock()
    const [state, setState] = useState('Обновить')
    const handlerClick = (callback: string) => setState(callback)
    const value = {
        setState: handlerClick,
        state
    }
    return (
        <ControlPanelContext.Provider value={value}>
            <div className={styles['panel-controlPanel']}>
                <div className={styles['panel-tabPanel']}>
                    {items}
                </div>

            </div>
        </ControlPanelContext.Provider>
    )
}
