import { useEffect, useState } from "react"
import backend from "../Api/brandsApi"
import { IBrand, IBrandDto } from "../Api/dto/IBrand"

export default function useBrand() {

    const defaultValue: IBrandDto[] = []
    const [brands, setBrands] = useState(defaultValue)

    useEffect(() => {
        backend.getBrands()
            .then(brands => setBrands(brands))
    }, [])

    const brand = (title: string) => {
        if (title === 'all')
            return brands
        else
            return brands.filter(brand => (brand.title === title) ? brand : '')
    }

    const createBrand = async (brand: IBrand) => {
        await backend.createBrand(brand)
            .then(brand => setBrands([...brands, brand]))
    }

    const updateBrand = async (brandP: IBrandDto) => {
        await backend.updateBrand(brandP)
            .then(newBrand => {
                const newBrands = brands.filter(brand => (brand.id !== brandP.id))
                setBrands([...newBrands, newBrand])
            })
    }

    const deleteBrand = async (title: string) => {
        await backend.deleteBrand(title)
            .then(() => {
                const newBrands = brands.filter(brand => (brand.title !== title))
                setBrands([...newBrands])
            })
    }

    return {
        brands,
        brand,
        createBrand,
        updateBrand,
        deleteBrand
    }
}
