import { useState } from "react"

export default function useHover() {
    const [isHovered, setHoverd] = useState(false)

    const hendlerMouseEnter = () => { setHoverd(!isHovered) }
    const hendlerMouseLeave = () => { setHoverd(!isHovered) }

    let style = { background: '#e8e8e8' }
    style.background = isHovered ? '#bdbdbd' : '#e8e8e8'

    return {
        handlers: [hendlerMouseEnter, hendlerMouseLeave],
        style: style
    }
}
