import { useState } from "react"

export default function useHoverAndClik() {
    const [isHovered, setHovered] = useState(false)

    const handlerMouseEnter = () => { setHovered(!isHovered) }
    const handlerMouseLeave = () => { setHovered(!isHovered) }

    let style = { background: '#e8e8e8' }
    style.background = isHovered ? '#bdbdbd' : '#e8e8e8'

    return {
        handlers: [handlerMouseEnter, handlerMouseLeave],
        style: style
    }
}
