import { createContext, useState } from "react";

const [blocked, setBlocked] = useState(false)
const block = () => setBlocked(!blocked)

let defaultValue = {
    block: block,
    blocked: blocked
}

const MenuContext = createContext(defaultValue)

export default MenuContext
