import styles from './Authorization.module.scss'
import { ScreenVersion } from '../../../Enums/headerEnums'
import { useState } from 'react'

interface AuthorizationProps {
    role?: string,
    version: ScreenVersion
}

export default function Authorization(props: AuthorizationProps) {
    const { version } = props

    const [isHoverd, setHoverd] = useState(false)

    const hendlerMouseEnter = () => { setHoverd(!isHoverd) }
    const hendlerMouseOut = () => { setHoverd(!isHoverd) }

    let style = { background: '#e8e8e8' }
    style.background = isHoverd ? '#e8e8e8' : '262626'

    return (
        <div
            className={styles['header-autorization__' + version]}
            onMouseEnter={hendlerMouseEnter}
            onMouseOut={hendlerMouseOut}
        >

        </div>
    )
}
